<?php// Require Composer's auto loading fileif (file_exists($composer = __DIR__ . '/vendor/autoload.php'))    require_once($composer);/** * Here's what's happening with these hooks: * 1. WordPress detects theme in themes/ahoy * 2. When we activate, we tell WordPress that the theme is actually in themes/ahoy/templates * 3. When we call get_template_directory() or get_template_directory_uri(), we point it back to themes/ahoy * * We do this so that the Template Hierarchy will look in themes/ahoy/templates for core WordPress themes * But functions.php, style.css, and index.php are all still located in themes/ahoy * * get_template_directory()   -> /srv/www/example.com/wp-content/themes/ahoy * get_stylesheet_directory() -> /srv/www/example.com/wp-content/themes/ahoy * locate_template() * ├── STYLESHEETPATH         -> /srv/www/example.com/wp-content/themes/ahoy * └── TEMPLATEPATH           -> /srv/www/example.com/wp-content/themes/ahoy/templates */add_filter('template', function ($stylesheet) {    return dirname($stylesheet);});add_action('after_switch_theme', function () {    $stylesheet = get_option('template');    if (basename($stylesheet) !== 'templates') {        update_option('template', $stylesheet . '/templates');    }});// Require theme functionsrequire_once('components/functions/functions-helpers.php');require_once('components/functions/functions-reset.php');require_once('components/functions/functions-admin.php');require_once('components/functions/functions-theme.php');require_once('components/functions/functions-cpt.php');require_once('components/functions/functions-email.php');require_once('components/functions/functions-newsletter.php');require_once('components/functions/functions-social.php');if ( is_plugin_activated('woocommerce') )	require_once('components/functions/functions-woocommerce.php');if ( is_plugin_activated('acf') )	require_once('components/functions/functions-acf.php');